// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IClayRoyalty {
    function recordRoyalties(string memory projectId, uint256 price, uint8 paymentToken) external payable;
    function calculateTotalRoyalties(string memory projectId) external view returns (uint256 totalETH, uint256 totalUSDC);
}

/**
 * @title ClayLibrary
 * @notice Smart contract for managing Clay project library assets
 * @dev Handles project registration, ownership tracking, and revenue distribution
 * @dev Supports ETH and USDC payments with royalty distribution
 */
contract ClayLibrary is Ownable2Step, ReentrancyGuard {
    // USDC token address on Base Mainnet
    address public constant USDC_ADDRESS = 0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913;
    IERC20 public immutable usdcToken;

    // Royalty tracker contract
    IClayRoyalty public royaltyContract;

    // Approved marketplaces (for transferAssetOwnership)
    mapping(address => bool) public approvedMarketplaces;

    enum PaymentToken { ETH, USDC }

    struct LibraryAsset {
        string projectId;           // Project ID on Irys
        string name;                // Asset name
        string description;         // Asset description
        uint256 royaltyPerImportETH;  // Royalty per import in ETH (wei)
        uint256 royaltyPerImportUSDC; // Royalty per import in USDC (6 decimals)
        address currentOwner;       // Current owner (can change via marketplace)
        address originalCreator;    // Original creator (never changes)
        uint256 listedAt;           // Timestamp when listed
        bool exists;                // Whether the asset exists (false = deleted)
        bool royaltyEnabled;        // Whether royalty is active (can be disabled by owner)
    }

    // Mapping from projectId to LibraryAsset
    mapping(string => LibraryAsset) public libraryAssets;

    // Mapping from user address to their owned assets
    mapping(address => string[]) public userOwnedAssets;

    // Array of all registered asset IDs
    string[] public allAssetIds;

    // Platform fee percentage (e.g., 250 = 2.5%)
    uint256 public platformFeePercentage = 250; // 2.5%
    uint256 public constant FEE_DENOMINATOR = 10000;

    // Events
    event AssetRegistered(
        string indexed projectId,
        address indexed creator,
        string name,
        uint256 price
    );

    event OwnershipTransferred(
        string indexed projectId,
        address indexed previousOwner,
        address indexed newOwner
    );

    event RoyaltyFeeUpdated(
        string indexed projectId,
        uint256 newRoyaltyETH,
        uint256 newRoyaltyUSDC
    );

    event RoyaltyDisabled(
        string indexed projectId,
        address indexed owner
    );

    event AssetDeleted(
        string indexed projectId,
        address indexed owner
    );

    constructor(address _royaltyContract) Ownable(msg.sender) {
        usdcToken = IERC20(USDC_ADDRESS);
        royaltyContract = IClayRoyalty(_royaltyContract);
    }

    /**
     * @notice Update royalty contract address (only owner)
     */
    function setRoyaltyContract(address _royaltyContract) external onlyOwner {
        royaltyContract = IClayRoyalty(_royaltyContract);
    }

    /**
     * @notice Set approved marketplace for ownership transfers (only owner)
     * @param marketplace The marketplace address
     * @param approved Whether the marketplace is approved
     */
    function setApprovedMarketplace(address marketplace, bool approved) external onlyOwner {
        approvedMarketplaces[marketplace] = approved;
    }

    /**
     * @notice Register a new library asset
     * @param projectId The Irys project ID
     * @param name Asset name
     * @param description Asset description
     * @param royaltyPerImportETH Royalty per import in ETH (wei)
     * @param royaltyPerImportUSDC Royalty per import in USDC (6 decimals)
     * @dev TODO: Add dependency-based minimum price validation on-chain
     *      Currently validated client-side in AdvancedClay.tsx
     *      Future enhancement: Add dependencyIds[] parameter and verify royalty > sum(dependencies)
     */
    function registerAsset(
        string memory projectId,
        string memory name,
        string memory description,
        uint256 royaltyPerImportETH,
        uint256 royaltyPerImportUSDC
    ) external {
        require(bytes(projectId).length > 0, "Project ID cannot be empty");
        // FIX: Allow free libraries (0 ETH, 0 USDC) for community contributions
        // require(royaltyPerImportETH > 0 || royaltyPerImportUSDC > 0, "At least one royalty must be set");
        require(!libraryAssets[projectId].exists, "Asset already registered");

        LibraryAsset memory newAsset = LibraryAsset({
            projectId: projectId,
            name: name,
            description: description,
            royaltyPerImportETH: royaltyPerImportETH,
            royaltyPerImportUSDC: royaltyPerImportUSDC,
            currentOwner: msg.sender,
            originalCreator: msg.sender,
            listedAt: block.timestamp,
            exists: true,
            royaltyEnabled: true
        });

        libraryAssets[projectId] = newAsset;
        userOwnedAssets[msg.sender].push(projectId);
        allAssetIds.push(projectId);

        emit AssetRegistered(projectId, msg.sender, name, royaltyPerImportETH);
    }

    /**
     * @notice Update royalty fees (only by current owner)
     * @param projectId The project ID
     * @param newRoyaltyETH New royalty per import in ETH
     * @param newRoyaltyUSDC New royalty per import in USDC
     */
    function updateRoyaltyFee(
        string memory projectId,
        uint256 newRoyaltyETH,
        uint256 newRoyaltyUSDC
    ) external {
        LibraryAsset storage asset = libraryAssets[projectId];
        require(asset.exists, "Asset not found");
        require(msg.sender == asset.currentOwner, "Only owner can update royalty");
        // FIX: Allow free libraries (0 ETH, 0 USDC)
        // require(newRoyaltyETH > 0 || newRoyaltyUSDC > 0, "At least one royalty must be set");

        asset.royaltyPerImportETH = newRoyaltyETH;
        asset.royaltyPerImportUSDC = newRoyaltyUSDC;

        emit RoyaltyFeeUpdated(projectId, newRoyaltyETH, newRoyaltyUSDC);
    }

    /**
     * @notice Get royalty fee for a library asset
     * @param projectId The project ID
     */
    function getRoyaltyFee(string memory projectId) external view returns (uint256 royaltyETH, uint256 royaltyUSDC) {
        LibraryAsset storage asset = libraryAssets[projectId];

        // Return 0 if royalty is disabled
        if (!asset.royaltyEnabled) {
            return (0, 0);
        }

        return (asset.royaltyPerImportETH, asset.royaltyPerImportUSDC);
    }

    // Purchase functions removed - all transactions through Marketplace only

    /**
     * @notice Transfer ownership of a library asset (for marketplace sales)
     * @param projectId The project ID
     * @param newOwner The new owner address
     */
    function transferAssetOwnership(
        string memory projectId,
        address newOwner
    ) external {
        LibraryAsset storage asset = libraryAssets[projectId];
        require(asset.exists, "Asset not found");
        require(
            msg.sender == asset.currentOwner || approvedMarketplaces[msg.sender],
            "Not authorized to transfer"
        );
        require(newOwner != address(0), "Invalid new owner");

        address previousOwner = asset.currentOwner;

        // Remove from previous owner's list
        _removeFromUserAssets(previousOwner, projectId);

        // Update owner
        asset.currentOwner = newOwner;

        // Add to new owner's list
        userOwnedAssets[newOwner].push(projectId);

        emit OwnershipTransferred(projectId, previousOwner, newOwner);
    }

    // Asset price update removed - royalty fees managed by updateRoyaltyFee instead

    /**
     * @notice Disable royalty for an asset (only by current owner)
     * @dev Asset remains tradable in marketplace, but no royalties will be charged on new uses
     * @param projectId The project ID
     */
    function disableRoyalty(string memory projectId) external {
        LibraryAsset storage asset = libraryAssets[projectId];
        require(asset.exists, "Asset not found");
        require(asset.royaltyEnabled, "Royalty already disabled");
        require(msg.sender == asset.currentOwner, "Only owner can disable royalty");

        asset.royaltyEnabled = false;

        emit RoyaltyDisabled(projectId, msg.sender);
    }

    /**
     * @notice Enable royalty for an asset (only by current owner)
     * @param projectId The project ID
     */
    function enableRoyalty(string memory projectId) external {
        LibraryAsset storage asset = libraryAssets[projectId];
        require(asset.exists, "Asset not found");
        require(!asset.royaltyEnabled, "Royalty already enabled");
        require(msg.sender == asset.currentOwner, "Only owner can enable royalty");

        asset.royaltyEnabled = true;

        emit RoyaltyFeeUpdated(projectId, asset.royaltyPerImportETH, asset.royaltyPerImportUSDC);
    }

    /**
     * @notice Delete an asset permanently (only by current owner)
     * @dev This makes the asset untradable and removes it from existence
     * @param projectId The project ID
     */
    function deleteAsset(string memory projectId) external {
        LibraryAsset storage asset = libraryAssets[projectId];
        require(asset.exists, "Asset not found");
        require(msg.sender == asset.currentOwner, "Only owner can delete");

        asset.exists = false;
        asset.royaltyEnabled = false;

        emit AssetDeleted(projectId, msg.sender);
    }

    /**
     * @notice Get asset details
     * @param projectId The project ID
     */
    function getAsset(string memory projectId) external view returns (LibraryAsset memory) {
        return libraryAssets[projectId];
    }

    /**
     * @notice Get current owner of an asset
     * @param projectId The project ID
     * @return currentOwner address, or address(0) if asset doesn't exist
     */
    function getCurrentOwner(string memory projectId) external view returns (address) {
        LibraryAsset storage asset = libraryAssets[projectId];

        // Return zero address only if asset doesn't exist (deleted)
        if (!asset.exists) {
            return address(0);
        }

        return asset.currentOwner;
    }

    /**
     * @notice Get all assets owned by a user
     * @param user The user address
     */
    function getUserAssets(address user) external view returns (string[] memory) {
        return userOwnedAssets[user];
    }

    // Purchasers tracking removed - Marketplace handles all transactions

    /**
     * @notice Get total number of registered assets
     */
    function getTotalAssets() external view returns (uint256) {
        return allAssetIds.length;
    }

    /**
     * @notice Get asset ID by index
     * @param index The index
     */
    function getAssetIdByIndex(uint256 index) external view returns (string memory) {
        require(index < allAssetIds.length, "Index out of bounds");
        return allAssetIds[index];
    }

    // Platform fees removed - Marketplace handles all transactions and fees

    /**
     * @dev Internal function to remove an asset from user's list
     */
    function _removeFromUserAssets(address user, string memory projectId) private {
        string[] storage assets = userOwnedAssets[user];
        for (uint256 i = 0; i < assets.length; i++) {
            if (keccak256(bytes(assets[i])) == keccak256(bytes(projectId))) {
                assets[i] = assets[assets.length - 1];
                assets.pop();
                break;
            }
        }
    }
}

ClayMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IClayLibrary {
    function transferAssetOwnership(string memory projectId, address newOwner) external;
    function getAsset(string memory projectId) external view returns (
        string memory,
        string memory,
        string memory,
        uint256,
        uint256,
        address,
        address,
        uint256,
        bool,
        bool
    );
}

interface IClayRoyalty {
    function calculateTotalRoyalties(string memory projectId) external view returns (uint256 totalETH, uint256 totalUSDC);
    function totalRoyaltiesPaidETH(string memory projectId) external view returns (uint256);
    function totalRoyaltiesPaidUSDC(string memory projectId) external view returns (uint256);
}

/**
 * @title ClayMarketplace
 * @notice NFT-style marketplace for buying/selling Clay library assets
 * @dev Supports fixed-price listings and offer system
 */
contract ClayMarketplace is Ownable, ReentrancyGuard {
    IClayLibrary public libraryContract;
    IClayRoyalty public royaltyContract;

    // USDC token address on Base Mainnet
    address public constant USDC_ADDRESS = 0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913;
    IERC20 public immutable usdcToken;

    enum PaymentToken { ETH, USDC }

    struct Listing {
        string projectId;
        address seller;
        uint256 price;
        PaymentToken paymentToken;
        uint256 listedAt;
        bool isActive;
    }

    struct Offer {
        string projectId;
        address buyer;
        uint256 offerPrice;
        PaymentToken paymentToken;
        uint256 offeredAt;
        uint256 expiresAt;
        bool isActive;
    }

    // Mapping from projectId to Listing
    mapping(string => Listing) public listings;

    // Mapping from offerId to Offer
    mapping(uint256 => Offer) public offers;

    // Mapping from projectId to array of offer IDs
    mapping(string => uint256[]) public projectOffers;

    // Counter for offer IDs
    uint256 public nextOfferId = 1;

    // Array of all active listing IDs
    string[] public activeListings;

    // Platform fee percentage (e.g., 250 = 2.5%)
    uint256 public platformFeePercentage = 250;
    uint256 public constant FEE_DENOMINATOR = 10000;

    // Events
    event AssetListed(
        string indexed projectId,
        address indexed seller,
        uint256 price,
        uint256 timestamp
    );

    event AssetSold(
        string indexed projectId,
        address indexed seller,
        address indexed buyer,
        uint256 price,
        uint256 platformFee
    );

    event ListingCancelled(
        string indexed projectId,
        address indexed seller
    );

    event OfferCreated(
        uint256 indexed offerId,
        string indexed projectId,
        address indexed buyer,
        uint256 offerPrice,
        uint256 expiresAt
    );

    event OfferAccepted(
        uint256 indexed offerId,
        string indexed projectId,
        address indexed seller,
        address buyer,
        uint256 price
    );

    event OfferCancelled(
        uint256 indexed offerId,
        string indexed projectId,
        address indexed buyer
    );

    event OfferRefundFailed(
        uint256 indexed offerId,
        string indexed projectId,
        address indexed buyer,
        uint256 refundAmount
    );

    event ListingPriceUpdated(
        string indexed projectId,
        uint256 oldPrice,
        uint256 newPrice
    );

    constructor(address _libraryContract, address _royaltyContract) Ownable(msg.sender) {
        libraryContract = IClayLibrary(_libraryContract);
        royaltyContract = IClayRoyalty(_royaltyContract);
        usdcToken = IERC20(USDC_ADDRESS);
    }

    /**
     * @notice Update royalty contract address (only owner)
     */
    function setRoyaltyContract(address _royaltyContract) external onlyOwner {
        royaltyContract = IClayRoyalty(_royaltyContract);
    }

    /**
     * @notice List an asset for sale at a fixed price
     * @param projectId The project ID
     * @param price Sale price
     * @param paymentToken Payment token (0: ETH, 1: USDC)
     * @dev CRITICAL SECURITY FIX: Validates price against PAID royalties (not current)
     *      This prevents underpricing even if libraries are deleted after project creation
     */
    function listAsset(string memory projectId, uint256 price, PaymentToken paymentToken) external {
        require(price > 0, "Price must be greater than 0");
        require(!listings[projectId].isActive, "Asset already listed");

        // Verify caller owns the asset in library
        (,,,,,address currentOwner, address originalCreator, uint256 listedAt, bool exists, bool royaltyEnabled) = libraryContract.getAsset(projectId);
        require(exists, "Asset not found in library");
        require(currentOwner == msg.sender, "Only owner can list asset");

        // CRITICAL SECURITY FIX: Validate price against PAID royalties (not current state)
        // This prevents selling below cost even if libraries are deleted/disabled
        // Uses totalRoyaltiesPaid instead of calculateTotalRoyalties
        if (address(royaltyContract) != address(0)) {
            // Get PAID royalties from ClayRoyalty contract
            uint256 paidETH = royaltyContract.totalRoyaltiesPaidETH(projectId);
            uint256 paidUSDC = royaltyContract.totalRoyaltiesPaidUSDC(projectId);

            if (paymentToken == PaymentToken.ETH) {
                // Price must be HIGHER than royalties PAID (not current state)
                // This protects creators even if their libraries are later deleted
                require(price > paidETH, "Price must be higher than royalties paid");
            } else {
                require(price > paidUSDC, "Price must be higher than royalties paid");
            }
        }

        Listing memory newListing = Listing({
            projectId: projectId,
            seller: msg.sender,
            price: price,
            paymentToken: paymentToken,
            listedAt: block.timestamp,
            isActive: true
        });

        listings[projectId] = newListing;
        activeListings.push(projectId);

        emit AssetListed(projectId, msg.sender, price, block.timestamp);
    }

    /**
     * @notice Buy an asset at the listed price
     * @param projectId The project ID
     */
    function buyAsset(string memory projectId) external payable nonReentrant {
        Listing storage listing = listings[projectId];
        require(listing.isActive, "Listing not active");
        require(msg.sender != listing.seller, "Cannot buy your own asset");

        // Calculate fees
        uint256 platformFee = (listing.price * platformFeePercentage) / FEE_DENOMINATOR;
        uint256 sellerPayment = listing.price - platformFee;

        if (listing.paymentToken == PaymentToken.ETH) {
            // FIX: Require exact amount to prevent refund failures
            require(msg.value == listing.price, "Exact ETH amount required");

            // Transfer ETH to seller
            (bool success, ) = listing.seller.call{value: sellerPayment}("");
            require(success, "Payment to seller failed");
        } else {
            require(msg.value == 0, "Do not send ETH for USDC purchase");

            // Transfer USDC to seller
            require(
                usdcToken.transferFrom(msg.sender, listing.seller, sellerPayment),
                "Payment to seller failed"
            );

            // Transfer platform fee
            require(
                usdcToken.transferFrom(msg.sender, address(this), platformFee),
                "Platform fee transfer failed"
            );
        }

        // Transfer ownership in library contract
        libraryContract.transferAssetOwnership(projectId, msg.sender);

        // Deactivate listing
        listing.isActive = false;
        _removeFromActiveListings(projectId);

        // Cancel all active offers for this asset
        _cancelAllOffers(projectId);

        emit AssetSold(projectId, listing.seller, msg.sender, listing.price, platformFee);
    }

    /**
     * @notice Cancel a listing
     * @param projectId The project ID
     * @dev CRITICAL FIX: Also cancels all active offers and refunds buyers
     */
    function cancelListing(string memory projectId) external {
        Listing storage listing = listings[projectId];
        require(listing.isActive, "Listing not active");
        require(msg.sender == listing.seller, "Only seller can cancel");

        listing.isActive = false;
        _removeFromActiveListings(projectId);

        // CRITICAL FIX: Cancel all active offers for this asset and refund buyers
        // This prevents buyers' funds from being locked when seller cancels listing
        _cancelAllOffers(projectId);

        emit ListingCancelled(projectId, msg.sender);
    }

    /**
     * @notice Update listing price
     * @param projectId The project ID
     * @param newPrice New price
     */
    function updateListingPrice(string memory projectId, uint256 newPrice) external {
        Listing storage listing = listings[projectId];
        require(listing.isActive, "Listing not active");
        require(msg.sender == listing.seller, "Only seller can update");
        require(newPrice > 0, "Price must be greater than 0");

        uint256 oldPrice = listing.price;
        listing.price = newPrice;

        emit ListingPriceUpdated(projectId, oldPrice, newPrice);
    }

    /**
     * @notice Make an offer for an asset
     * @param projectId The project ID
     * @param offerPrice Offer price
     * @param paymentToken Payment token (0: ETH, 1: USDC)
     * @param duration Offer validity duration in seconds
     */
    function makeOffer(
        string memory projectId,
        uint256 offerPrice,
        PaymentToken paymentToken,
        uint256 duration
    ) external payable {
        require(offerPrice > 0, "Offer must have value");
        require(duration >= 1 hours && duration <= 30 days, "Invalid duration");

        // Verify asset exists in library
        (,,,,,address currentOwner, address originalCreator, uint256 listedAt, bool exists, bool royaltyEnabled) = libraryContract.getAsset(projectId);
        require(exists, "Asset not found in library");
        require(currentOwner != msg.sender, "Cannot offer on your own asset");

        // Handle payment based on token type
        if (paymentToken == PaymentToken.ETH) {
            require(msg.value == offerPrice, "ETH amount mismatch");
        } else {
            require(msg.value == 0, "Do not send ETH for USDC offer");
            // Transfer USDC to contract as escrow
            require(
                usdcToken.transferFrom(msg.sender, address(this), offerPrice),
                "USDC transfer failed"
            );
        }

        uint256 offerId = nextOfferId++;
        uint256 expiresAt = block.timestamp + duration;

        Offer memory newOffer = Offer({
            projectId: projectId,
            buyer: msg.sender,
            offerPrice: offerPrice,
            paymentToken: paymentToken,
            offeredAt: block.timestamp,
            expiresAt: expiresAt,
            isActive: true
        });

        offers[offerId] = newOffer;
        projectOffers[projectId].push(offerId);

        emit OfferCreated(offerId, projectId, msg.sender, offerPrice, expiresAt);
    }

    /**
     * @notice Accept an offer
     * @param offerId The offer ID
     */
    function acceptOffer(uint256 offerId) external nonReentrant {
        Offer storage offer = offers[offerId];
        require(offer.isActive, "Offer not active");
        require(block.timestamp < offer.expiresAt, "Offer expired");

        // Verify caller owns the asset
        (,,,,,address currentOwner, address originalCreator, uint256 listedAt, bool exists, bool royaltyEnabled) = libraryContract.getAsset(offer.projectId);
        require(exists, "Asset not found in library");
        require(currentOwner == msg.sender, "Only owner can accept offer");

        // Calculate fees
        uint256 platformFee = (offer.offerPrice * platformFeePercentage) / FEE_DENOMINATOR;
        uint256 sellerPayment = offer.offerPrice - platformFee;

        // Transfer payment to seller based on token type
        if (offer.paymentToken == PaymentToken.ETH) {
            (bool success, ) = msg.sender.call{value: sellerPayment}("");
            require(success, "Payment to seller failed");
        } else {
            require(
                usdcToken.transfer(msg.sender, sellerPayment),
                "Payment to seller failed"
            );
        }

        // Transfer ownership in library contract
        libraryContract.transferAssetOwnership(offer.projectId, offer.buyer);

        // Deactivate offer
        offer.isActive = false;

        // Cancel listing if exists
        if (listings[offer.projectId].isActive) {
            listings[offer.projectId].isActive = false;
            _removeFromActiveListings(offer.projectId);
        }

        // Cancel all other offers for this asset
        _cancelAllOffers(offer.projectId);

        emit OfferAccepted(offerId, offer.projectId, msg.sender, offer.buyer, offer.offerPrice);
        emit AssetSold(offer.projectId, msg.sender, offer.buyer, offer.offerPrice, platformFee);
    }

    /**
     * @notice Cancel an offer and get refund
     * @param offerId The offer ID
     */
    function cancelOffer(uint256 offerId) external nonReentrant {
        Offer storage offer = offers[offerId];
        require(offer.isActive, "Offer not active");
        require(msg.sender == offer.buyer, "Only buyer can cancel offer");

        uint256 refundAmount = offer.offerPrice;
        offer.isActive = false;

        // Refund based on payment token
        if (offer.paymentToken == PaymentToken.ETH) {
            (bool success, ) = msg.sender.call{value: refundAmount}("");
            require(success, "Refund failed");
        } else {
            require(
                usdcToken.transfer(msg.sender, refundAmount),
                "Refund failed"
            );
        }

        emit OfferCancelled(offerId, offer.projectId, msg.sender);
    }

    /**
     * @notice Get all offers for a project
     * @param projectId The project ID
     */
    function getProjectOffers(string memory projectId) external view returns (uint256[] memory) {
        return projectOffers[projectId];
    }

    /**
     * @notice Get active listings count
     */
    function getActiveListingsCount() external view returns (uint256) {
        return activeListings.length;
    }

    /**
     * @notice Update platform fee (only owner)
     */
    function updatePlatformFee(uint256 newFeePercentage) external onlyOwner {
        require(newFeePercentage <= 1000, "Fee too high (max 10%)");
        platformFeePercentage = newFeePercentage;
    }

    /**
     * @notice Withdraw platform fees (only owner)
     */
    function withdrawPlatformFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");

        (bool success, ) = owner().call{value: balance}("");
        require(success, "Withdrawal failed");
    }

    /**
     * @dev Internal function to remove from active listings
     */
    function _removeFromActiveListings(string memory projectId) private {
        for (uint256 i = 0; i < activeListings.length; i++) {
            if (keccak256(bytes(activeListings[i])) == keccak256(bytes(projectId))) {
                activeListings[i] = activeListings[activeListings.length - 1];
                activeListings.pop();
                break;
            }
        }
    }

    /**
     * @dev Internal function to cancel all offers for a project
     */
    function _cancelAllOffers(string memory projectId) private {
        uint256[] storage offerIds = projectOffers[projectId];
        for (uint256 i = 0; i < offerIds.length; i++) {
            Offer storage offer = offers[offerIds[i]];
            if (offer.isActive) {
                offer.isActive = false;

                // Refund based on payment token
                bool success = false;
                if (offer.paymentToken == PaymentToken.ETH) {
                    (success, ) = offer.buyer.call{value: offer.offerPrice}("");
                } else {
                    success = usdcToken.transfer(offer.buyer, offer.offerPrice);
                }

                // FIX: Emit event even if refund fails, so user knows to claim manually
                if (success) {
                    emit OfferCancelled(offerIds[i], projectId, offer.buyer);
                } else {
                    emit OfferRefundFailed(offerIds[i], projectId, offer.buyer, offer.offerPrice);
                }
            }
        }
    }

    /**
     * @notice Withdraw platform fees in USDC (only owner)
     */
    function withdrawPlatformFeesUSDC() external onlyOwner {
        uint256 balance = usdcToken.balanceOf(address(this));
        require(balance > 0, "No USDC fees to withdraw");

        require(usdcToken.transfer(owner(), balance), "Withdrawal failed");
    }
}

ClayRoyalty.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IClayLibrary {
    function getCurrentOwner(string memory projectId) external view returns (address);
    function getRoyaltyFee(string memory projectId) external view returns (uint256 royaltyETH, uint256 royaltyUSDC);
}

/**
 * @title ClayRoyalty
 * @notice Tracks library dependencies and enforces minimum pricing with royalty distribution
 * @dev Ensures creators can't undercut by enforcing price floor based on used libraries
 */
contract ClayRoyalty is Ownable, ReentrancyGuard {
    // USDC token
    address public constant USDC_ADDRESS = 0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913;
    IERC20 public immutable usdcToken;

    // Library contract reference
    IClayLibrary public libraryContract;

    // Pull Pattern: Pending royalties
    mapping(address => uint256) public pendingRoyaltiesETH;
    mapping(address => uint256) public pendingRoyaltiesUSDC;

    enum PaymentToken { ETH, USDC }

    struct LibraryDependency {
        string dependencyProjectId; // Dependency project ID
        uint256 royaltyPercentage;  // Royalty percentage (1000 = 10%)
        uint256 fixedRoyaltyETH;    // Fixed royalty in ETH (based on dependency price at registration)
        uint256 fixedRoyaltyUSDC;   // Fixed royalty in USDC (based on dependency price at registration)
    }

    struct ProjectRoyalties {
        string projectId;
        LibraryDependency[] dependencies;
        bool hasRoyalties;
    }

    // Mapping from projectId to its royalty info
    mapping(string => ProjectRoyalties) public projectRoyalties;

    // Royalty percentage (10%)
    uint256 public constant ROYALTY_PERCENTAGE = 1000; // 10%
    uint256 public constant PERCENTAGE_DENOMINATOR = 10000;

    // CRITICAL FIX: Track total royalties actually paid for each project
    // This prevents marketplace underpricing by ensuring sale price > paid royalties
    mapping(string => uint256) public totalRoyaltiesPaidETH;
    mapping(string => uint256) public totalRoyaltiesPaidUSDC;

    // Events
    event RoyaltiesRegistered(
        string indexed projectId,
        uint256 dependencyCount
    );

    event RoyaltyRecorded(
        string indexed projectId,
        address indexed recipient,
        uint256 amountETH,
        uint256 amountUSDC
    );

    event RoyaltyClaimed(
        address indexed claimant,
        uint256 amountETH,
        uint256 amountUSDC
    );

    constructor(address _libraryContract) Ownable(msg.sender) {
        usdcToken = IERC20(USDC_ADDRESS);
        libraryContract = IClayLibrary(_libraryContract);
    }

    /**
     * @notice Update library contract address (only owner)
     */
    function setLibraryContract(address _libraryContract) external onlyOwner {
        libraryContract = IClayLibrary(_libraryContract);
    }

    /**
     * @notice Register library dependencies for a project
     * @param projectId The project being registered
     * @param dependencyProjectIds Array of library project IDs used
     */
    function registerProjectRoyalties(
        string memory projectId,
        string[] memory dependencyProjectIds
    ) external {
        require(!projectRoyalties[projectId].hasRoyalties, "Royalties already registered");

        ProjectRoyalties storage royalty = projectRoyalties[projectId];
        royalty.projectId = projectId;
        royalty.hasRoyalties = true;

        for (uint256 i = 0; i < dependencyProjectIds.length; i++) {
            // Get current royalty fee from Library at registration time
            (uint256 feeETH, uint256 feeUSDC) = libraryContract.getRoyaltyFee(dependencyProjectIds[i]);

            LibraryDependency memory dep = LibraryDependency({
                dependencyProjectId: dependencyProjectIds[i],
                royaltyPercentage: 10000, // Not used anymore, kept for compatibility
                fixedRoyaltyETH: feeETH,
                fixedRoyaltyUSDC: feeUSDC
            });

            royalty.dependencies.push(dep);
        }

        emit RoyaltiesRegistered(projectId, dependencyProjectIds.length);
    }

    /**
     * @notice Calculate total royalties for a project (fixed amounts)
     * @dev FIX: Only counts active libraries (not deleted ones)
     * @param projectId The project ID
     * @return totalETH Total ETH royalties from active libraries
     * @return totalUSDC Total USDC royalties from active libraries
     */
    function calculateTotalRoyalties(string memory projectId) public view returns (uint256 totalETH, uint256 totalUSDC) {
        ProjectRoyalties storage royalty = projectRoyalties[projectId];

        if (!royalty.hasRoyalties) {
            return (0, 0);
        }

        // FIX: Sum up fixed royalties only for existing libraries
        for (uint256 i = 0; i < royalty.dependencies.length; i++) {
            LibraryDependency memory dep = royalty.dependencies[i];

            // Check if library still exists
            address owner = libraryContract.getCurrentOwner(dep.dependencyProjectId);

            // Only count if library exists (owner != 0)
            if (owner != address(0)) {
                totalETH += dep.fixedRoyaltyETH;
                totalUSDC += dep.fixedRoyaltyUSDC;
            }
        }

        return (totalETH, totalUSDC);
    }

    /**
     * @notice Record royalties when a project is purchased (Pull Pattern)
     * @param projectId The project being purchased
     * @param price The price paid (unused, kept for interface compatibility)
     * @param paymentToken Payment token used (0: ETH, 1: USDC)
     */
    function recordRoyalties(
        string memory projectId,
        uint256 price,
        PaymentToken paymentToken
    ) external payable nonReentrant {
        ProjectRoyalties storage royalty = projectRoyalties[projectId];
        require(royalty.hasRoyalties, "No royalties for this project");

        if (paymentToken == PaymentToken.ETH) {
            require(msg.value > 0, "No ETH sent");

            // FIX: Calculate actual total needed (excluding deleted libraries)
            uint256 totalETHNeeded = 0;
            for (uint256 i = 0; i < royalty.dependencies.length; i++) {
                LibraryDependency memory dep = royalty.dependencies[i];
                address owner = libraryContract.getCurrentOwner(dep.dependencyProjectId);

                // Only count if library still exists (owner != 0)
                if (dep.fixedRoyaltyETH > 0 && owner != address(0)) {
                    totalETHNeeded += dep.fixedRoyaltyETH;
                }
            }

            require(msg.value >= totalETHNeeded, "Insufficient ETH sent");

            // Record fixed royalties for each dependency
            for (uint256 i = 0; i < royalty.dependencies.length; i++) {
                LibraryDependency memory dep = royalty.dependencies[i];

                // Get current owner of dependency (dynamic ownership support)
                address owner = libraryContract.getCurrentOwner(dep.dependencyProjectId);

                // Use fixed royalty amount (calculated at registration time)
                uint256 royaltyAmount = dep.fixedRoyaltyETH;

                if (royaltyAmount > 0 && owner != address(0)) {
                    pendingRoyaltiesETH[owner] += royaltyAmount;
                    emit RoyaltyRecorded(projectId, owner, royaltyAmount, 0);
                }
            }

            // CRITICAL FIX: Store total royalties paid for marketplace minimum price validation
            totalRoyaltiesPaidETH[projectId] = totalETHNeeded;

            // Refund excess ETH if any
            if (msg.value > totalETHNeeded) {
                (bool success, ) = msg.sender.call{value: msg.value - totalETHNeeded}("");
                require(success, "Refund failed");
            }
        } else {
            require(msg.value == 0, "Do not send ETH for USDC royalties");

            // FIX: Calculate total USDC needed (excluding deleted libraries)
            uint256 totalUSDC = 0;
            for (uint256 i = 0; i < royalty.dependencies.length; i++) {
                LibraryDependency memory dep = royalty.dependencies[i];
                address owner = libraryContract.getCurrentOwner(dep.dependencyProjectId);

                // Only count if library still exists (owner != 0)
                if (dep.fixedRoyaltyUSDC > 0 && owner != address(0)) {
                    totalUSDC += dep.fixedRoyaltyUSDC;
                }
            }

            require(totalUSDC > 0, "No USDC royalties for this project");

            // Transfer USDC from payer to this contract
            require(
                usdcToken.transferFrom(msg.sender, address(this), totalUSDC),
                "USDC transfer to contract failed"
            );

            // Record fixed USDC royalties for each dependency
            for (uint256 i = 0; i < royalty.dependencies.length; i++) {
                LibraryDependency memory dep = royalty.dependencies[i];

                // Get current owner of dependency (dynamic ownership support)
                address owner = libraryContract.getCurrentOwner(dep.dependencyProjectId);

                // Use fixed royalty amount (calculated at registration time)
                uint256 royaltyAmount = dep.fixedRoyaltyUSDC;

                if (royaltyAmount > 0 && owner != address(0)) {
                    pendingRoyaltiesUSDC[owner] += royaltyAmount;
                    emit RoyaltyRecorded(projectId, owner, 0, royaltyAmount);
                }
            }

            // CRITICAL FIX: Store total royalties paid for marketplace minimum price validation
            totalRoyaltiesPaidUSDC[projectId] = totalUSDC;
        }
    }

    /**
     * @notice Claim pending ETH royalties (Pull Pattern)
     */
    function claimRoyaltiesETH() external nonReentrant {
        uint256 pending = pendingRoyaltiesETH[msg.sender];
        require(pending > 0, "No pending ETH royalties");

        pendingRoyaltiesETH[msg.sender] = 0;

        (bool success, ) = msg.sender.call{value: pending}("");
        require(success, "ETH transfer failed");

        emit RoyaltyClaimed(msg.sender, pending, 0);
    }

    /**
     * @notice Claim pending USDC royalties (Pull Pattern)
     */
    function claimRoyaltiesUSDC() external nonReentrant {
        uint256 pending = pendingRoyaltiesUSDC[msg.sender];
        require(pending > 0, "No pending USDC royalties");

        pendingRoyaltiesUSDC[msg.sender] = 0;

        require(usdcToken.transfer(msg.sender, pending), "USDC transfer failed");

        emit RoyaltyClaimed(msg.sender, 0, pending);
    }

    /**
     * @notice Get pending royalties for an address
     * @param account The account to check
     */
    function getPendingRoyalties(address account) external view returns (uint256 eth, uint256 usdc) {
        return (pendingRoyaltiesETH[account], pendingRoyaltiesUSDC[account]);
    }

    /**
     * @notice Get all dependencies for a project
     * @param projectId The project ID
     */
    function getProjectDependencies(string memory projectId) external view returns (LibraryDependency[] memory) {
        return projectRoyalties[projectId].dependencies;
    }

    /**
     * @notice Fallback to receive ETH
     */
    receive() external payable {}
}

